model.metadata:
CREATE VIEW DatastoresView (id varchar(5), type varchar(128))
AS SELECT T.id, T.name FROM (EXEC MetadataSource.invokeHttp('GET', null, 'https://service.datadirectcloud.com/api/mgmt/datastores', 'TRUE')) AS f, XMLTABLE('/holder/dataStores' PASSING JSONTOXML('holder', f.result) COLUMNS id string PATH 'id/text()', name string PATH 'name/text()')as T;
CREATE VIEW DatasourcesView (id varchar(5), name varchar(128), datastoreid varchar(5)) AS SELECT T.id, T.name, T.datastoreid FROM (EXEC MetadataSource.invokeHttp('GET', null, 'https://service.datadirectcloud.com/api/mgmt/datasources', 'TRUE')) AS f, XMLTABLE('/holder/dataSources' PASSING JSONTOXML('holder', f.result) COLUMNS id string PATH 'id/text()', name string PATH 'name/text()', datastoreid string PATH 'dataStore/text()')as T;
